import React, { Component } from 'react'

const status = {
    'watched': 'Assistido',
    'watching': 'Assistindo',
    'toWatch': 'Assistir'
}
class NovaSerie extends Component{
    render(){
        return (
            <section className="intro-section">
                <h1>Nova Série</h1>
                <form>
                    Nome: <input type="text" className="form-control" /> <br />
                    Status: 
                    <select>
                        { Object
                            .keys(status)
                            .map(key => <option value={key}>{status[key]}</option>)
                        }
                    </select> <br />
                    Comentários: <textarea className="form-control" ></textarea> <br />
                </form>
            </section>
        )
    }
}

export default NovaSerie
