import React, { Component } from 'react'
import api from './Api'

const statusEnum = {
    'watched': 'Assistido',
    'watching': 'Assistindo',
    'toWatch': 'Assistir'
}

class Series extends Component{
    constructor(props){
        super(props)
        this.state = {
          series: [],
          isLoading: false
        }
    }

    componentDidMount(){
        this.setState({ isLoading: true })

        api.loadSeriesByGenre(this.props.match.params.genre)
            .then((res) => {
            this.setState({
                isLoading: false,
                series: res.data
            })
        })
    }

    renderSeries(series){
        return (
            <div key={series.id} className="item col-xs-4 col-lg-4">
                <div className="thumbnail">
                    <img className="group list-group-image" src="http://placehold.it/400x250/000/fff" alt="" />
                    <div className="caption">
                        <h4 className="group inner list-group-item-heading">
                            {series.name}</h4>
                        <div className="row">
                            <div className="col-xs-12 col-md-12">
                                <p className="lead">{series.genre} / {statusEnum[series.status]}</p>
                            </div>
                            <div className="col-xs-12 col-md-12">
                                <a className="btn btn-success" href="">Gerenciar</a>
                                <a className="btn btn-danger" onClick={() => console.log('excluir', series.id)}>Excluir</a>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
    render(){
        return (
            <section id="intro" className="intro-section">
                <h1>SÃ©rie {this.props.match.params.genre}</h1>
                <div id="series" className="row list-group">
                    {
                        !this.state.isLoading &&
                        this.state.series.map(this.renderSeries)
                    }
                </div>
            </section>
        )
    }
}

export default Series
