import React, { Component } from 'react'
import api from './Api'

const status = {
    'watched': 'Assistido',
    'watching': 'Assistindo',
    'toWatch': 'Assistir'
}
class NovaSerie extends Component{
    constructor(props){
        super(props)
        this.state = {
          genres: [],
          isLoading: false
        }

        this.saveSeries = this.saveSeries.bind(this)
    }

    componentDidMount(){
        this.setState({ isLoading: true })

        api.loadGenres()
            .then((res) => {
            this.setState({
                isLoading: false,
                genres: res.data
            })
        })
    }

    saveSeries(){
        const newSeries = {
            name: this.refs.nome.value,
            status: this.refs.status.value,
            genre: this.refs.genero.value,
            comments: this.refs.comentario.value
        }

        api.saveSeries(newSeries)
            .then((res) => console.log(res))
    }

    render(){
        return (
            <section className="intro-section">
                <h1>Nova Série</h1>
                <form>
                    Nome: <input type="text" ref='nome' className="form-control" /> <br />
                    Status: 
                    <select ref='status'>
                        { Object
                            .keys(status)
                            .map(key => <option key={key} value={key}>{status[key]}</option>)
                        }
                    </select> <br />
                    Gênero: 
                    <select ref='genero'>
                        { this.state.genres
                            .map(key => <option key={key} value={key}>{key}</option>)
                        }
                    </select> <br />
                    Comentários: <textarea ref='comentario' className="form-control" ></textarea> <br />
                    <button className='button' onClick={this.saveSeries}>Salvar</button>
                </form>
            </section>
        )
    }
}

export default NovaSerie
